mapper.select:
select s.*,sc.coursename,sc.courseclass,h.workname
from score s
left join selectcourse sc on s.selectcourseid = sc.selectcourseid
left join homework h on s.workid=h.workid

  where sc.teaid = #{teaid} and coursename=#{coursename}


  where sc.teaid =#{teaid} and sc.coursename=#{coursename} and sc.courseclass=#{courseclass}
